<span style="color:Blue;">foreach</span> ($objPHPExcel-&gt;getActiveSheet()-&gt;getRowIterator() <span style="color:Blue;">as</span> $row) {
    <span style="color:Blue;">if</span> ($objPHPExcel-&gt;getActiveSheet()-&gt;getRowDimension($row-&gt;getRowIndex())-&gt;getVisible()) {
