//load our new PHPExcel library<br />
<pre><code>    $this-&gt;load-&gt;library('excel');</code></pre>

//activate worksheet number 1<br />
<pre><code>    $this-&gt;excel-&gt;setActiveSheetIndex(0);</code></pre>

//name the worksheet<br />
<pre><code>    $this-&gt;excel-&gt;getActiveSheet()-&gt;setTitle('test worksheet');
</code></pre>

//set cell A1 content with some text<br />
<pre><code>    $this-&gt;excel-&gt;getActiveSheet()-&gt;setCellValue('A1', 'This');
    $this-&gt;excel-&gt;getActiveSheet()-&gt;setCellValue('B1', 'Test');</code></pre>

//change the font size<br />
<pre><code>    $this-&gt;excel-&gt;getActiveSheet()-&gt;getStyle('A1')-&gt;getFont()-&gt;setSize(10);</code></pre>

//make the font become bold<br />
<pre><code>    $this-&gt;excel-&gt;getActiveSheet()-&gt;getStyle('A1')-&gt;getFont()-&gt;setBold(true);</code></pre>

//merge cell A1 until D1      <br />
//set aligment to center for that merged cell (A1 to D1)<br />
<pre><code>    $this-&gt;excel-&gt;getActiveSheet()-&gt;getStyle('A1')-&gt;getAlignment()-&gt;setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_CENTER);

    $filename = 'aa.xls'; //save our workbook as this file name
    header('Content-Type: application/vnd.ms-excel'); //mime type
    header('Content-Disposition: attachment;filename=&quot;' . $filename . '&quot;'); //tell browser what's the file name
    header('Cache-Control: max-age=0'); //no cache        </code></pre>

//save it to Excel5 format (excel 2003 .XLS file), change this to 'Excel2007' (and adjust the filename extension, also the header mime type)<br />
//if you want to save it as .XLSX Excel 2007 format            
<br />
<pre><code>    $objWriter = PHPExcel_IOFactory::createWriter($this-&gt;excel, 'Excel5');</code></pre>

//force user to download the Excel file without writing it to server's HD<br />
<pre><code>    $objWriter-&gt;save('php://output');


</code></pre>

