    $sheets = array();

    $sheets[0] = $objPHPExcel->createSheet();
    $sheets[0]->setTitle('Page 1');

    $scol = 0;
    $srow = 1;

    foreach($sw['all']->result() as $row)
    {
        //log_message('info','Row: '.$srow);

        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->softwarename));
        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->source));
