        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->usedlics));
        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->inactive));
        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->doclocation));
        $sheets[0]->setCellValueByColumnAndRow($scol++,$srow, trim($row->notes));

        $scol = 0;
        $srow++;
    }

    header("Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet");
    header('Content-Disposition: attachment;filename="'.$filename.'"');
    header('Cache-Control: max-age=0');

    $objWriter = new PHPExcel_Writer_Excel2007($objPHPExcel);
    $objWriter->save($filename);
}
